<template>
    <div>
        <UpdateMedia
            :error="error"
            :server="server"
            :media-server="mediaServer + post_id"
            :media-file-path="mediaFilePath"
            
            @saved-media="getSavedMedia"
            @deleted-media='getDeletedMedia'
            @added-media='getAddedMedia'
        />
    </div>
</template>

<script>
import UpdateMedia from 'vue-media-upload';

export default {
    data(){
        return{
            post_id:7,

            error:'', //if it is not null it will apply the error styling and show the error message.
            server:'/api/upload', //the api that will temporary save the image.
            mediaServer:'/api/media/', //the api that will return the images names.
            mediaFilePath:'/storage/post_images', //the public file where the saved midia are saved.

            savedMedia:[], //saved media
            deletedMedia:[], //deleted media
            addedMedia:[], //the added images
        }
    },
    methods:{
        getSavedMedia(value){
            this.savedMedia=value
        },
        getDeletedMedia(value){
            this.deletedMedia=value
        },
        getAddedMedia(value){
            this.addedMedia=value
        },
    },
    components:{
        UpdateMedia
    }

}
</script>

